INSERT INTO `exam_test_sheet` VALUES ('2', '浙大城市学院计算学院第44期积极分子考试', '入党积极分子', '2022-11-17 10:30:00', '{\"id\": \"1\", \"checked\": false, \"question\": \"( )是社会主义法治最根本的保证。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"党的领导\",\"B\":\"公平正义\",\"C\":\"依法治国\",\"D\":\"社会和谐\"}};\r\n{\"id\": \"2\", \"checked\": false, \"question\": \"中国特色社会主义制度及其执行能力的集中体现是( )。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"国家治理体系和治理能力\",\"B\":\"党的领导制度体系\",\"C\":\"人民当家作主制度体系\",\"D\":\"中国特色社会主义行政体制\"}};\r\n{\"id\": \"3\", \"checked\": false, \"question\": \"新时代党的好干部标准是( )。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"信念坚定、为民服务、勤政务实、敢于担当、清正廉洁\",\"B\":\"有理想、有道德、有文化、有素质\",\"C\":\"年轻化、革命化、专业化\",\"D\":\"政治过硬、经济能干、文化素质高\"}};\r\n{\"id\": \"4\", \"checked\": false, \"question\": \"全面建成小康社会,“小康”讲的是( )。\", \"trueAnswer\": \"D\", \"options\": {\"A\":\"丰衣足食\",\"B\":\"国家富强\",\"C\":\"民族振兴\",\"D\":\"发展水平\"}};\r\n{\"id\": \"5\", \"checked\": false, \"question\": \"全面建成小康社会是中国共产党的第( )个百年奋斗目标。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"一\",\"B\":\"二\",\"C\":\"三\",\"D\":\"四\"}}', 0);
