INSERT INTO `multiple_option_question` VALUES ('53', '袁世凯就任临时大总统后破坏资产阶级民主制度的活动有()', 'ABCD', '{\"A\":\"强迫国会选举他为正式大总统\",\"B\":\"解散国会,遣散议员\",\"C\":\"炮制<中华民国约法>,用总统制取代内阁制\",\"D\":\"修改<总统选举法>以达到无限期连任,并推荐继承人\"}', '0');
