INSERT INTO `practice_answer_sheet` VALUES ('53e1ae06-bf81-4e8a-920a-e88fa8c05f9c', '2022年11月第一周答题', '32001251', '{\"id\": \"1\", \"checked\": false, \"question\": \"我们党站在时代前列,保持先进性的根本体现和根本要求是( )。\", \"trueAnswer\": \"C\", \"options\": {\"A\":\"始终代表中国先进生产力的发展要求,大力促进先进生产力的发展\",\"B\":\"始终代表中国先进文化的前进方向\",\"C\":\"始终代表中国最广大人民的根本利益\",\"D\":\"始终代表全世界最广大人民的根本利益\"}};\r\n{\"id\": \"2\", \"checked\": false, \"question\": \"科学发展观的核心立场是( )。\", \"trueAnswer\": \"B\", \"options\": {\"A\":\"为人民服务\",\"B\":\"以人为本\",\"C\":\"科学发展\",\"D\":\"以人民为中心\"}};\r\n{\"id\": \"3\", \"checked\": false, \"question\": \"在当代中国,坚持发展是硬道理的本质要求,就是坚持( )。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"科学发展\",\"B\":\"协调发展\",\"C\":\"可持续发展\",\"D\":\"全面发展\"}};\r\n{\"id\": \"4\", \"checked\": false, \"question\": \"科学发展观最鲜明的精神实质是( )。\", \"trueAnswer\": \"C\", \"options\": {\"A\":\"解放思想\",\"B\":\"解放思想、实事求是\",\"C\":\"解放思想、实事求是、与时俱进\",\"D\":\"解放思想、实事求是、与时俱进、求真务实\"}};\r\n{\"id\": \"5\", \"checked\": false, \"question\": \"( )是中国特色社会主义的本质属性。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"经济发达\",\"B\":\"政治清明\",\"C\":\"文化丰富\",\"D\":\"社会和谐\"}}', '0', '每周答题', '2022-11-22 15:02:12');
