INSERT INTO `pratice_test_sheet` VALUES ('2', '“2022年中国5G+工业互联网大会相关知识”专项答题', '专项答题', '{\"id\": \"1\", \"checked\": false, \"question\": \"我们党一切行动的根本出发点和落脚点是( )。\", \"trueAnswer\": \"C\", \"options\": {\"A\":\"密切联系群众\",\"B\":\"理论联系实际\",\"C\":\"全心全意为人民服务\",\"D\":\"实事求是\"}};\r\n{\"id\": \"2\", \"checked\": false, \"question\": \"邓小平曾经指出,和平问题是东西问题,( )问题是南北问题。\", \"trueAnswer\": \"B\", \"options\": {\"A\":\"战争\",\"B\":\"发展\",\"C\":\"稳定\",\"D\":\"经济发展\"}};\r\n{\"id\": \"3\", \"checked\": false, \"question\": \"坚持四项基本原则,回答了解放和发展生产力的( )问题。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"政治保证\",\"B\":\"政治保障\",\"C\":\"理论前提\",\"D\":\"发展动力\"}};\r\n{\"id\": \"4\", \"checked\": false, \"question\": \"在建设社会主义政治文明过程中,依法治国是党领导人民治理国家的( )。\", \"trueAnswer\": \"C\", \"options\": {\"A\":\"本质要求\",\"B\":\"重要前提\",\"C\":\"基本方略\",\"D\":\"核心问题\"}};\r\n{\"id\": \"5\", \"checked\": false, \"question\": \"毛泽东思想的精髓和根本出发点是( )。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"实事求是\",\"B\":\"群众路线\",\"C\":\"独立自主\",\"D\":\"党的建设\"}}', '2022-11-19');
INSERT INTO `pratice_test_sheet` VALUES ('3', '“学习党的二十大通过的决议精神”系列专项答题', '专项答题', '{\"id\": \"1\", \"checked\": false, \"question\": \"进入改革开放新时期以来,我们党农村政策的重要基石是( )。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"以家庭承包经营为基础、统分结合的双层经营体制\",\"B\":\"坚持农村土地集体所有\",\"C\":\"放开经营权\",\"D\":\"实现承包权和经营权分置并行\"}};\r\n{\"id\": \"2\", \"checked\": false, \"question\": \"富强、民主、文明、和谐,自由、平等、公正、法治,爱国、敬业、诚信、友善,是( )的集中体现。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"当代中国精神\",\"B\":\"当代世界精神\",\"C\":\"传统中国精神\",\"D\":\"传统世界精神\"}};\r\n{\"id\": \"3\", \"checked\": false, \"question\": \"中国特色社会主义进人了新时代,我国经济发展也进入了新时代,基本特征就是经济已由( )。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"高速增长阶段转向高质量发展阶段\",\"B\":\"中国制造向中国创造转变\",\"C\":\"中国速度向中国质量转变\",\"D\":\"制造大国向制造强国转变\"}};\r\n{\"id\": \"4\", \"checked\": false, \"question\": \"“五位一体”将勾勒出一幅( )的社会主义现代化强国壮美景象。\", \"trueAnswer\": \"D\", \"options\": {\"A\":\"富强文明自由和谐美丽\",\"B\":\"富裕民主文明和谐美丽\",\"C\":\"富强民主文明和谐生态\",\"D\":\"富强民主文明和谐美丽\"}};\r\n{\"id\": \"5\", \"checked\": false, \"question\": \"建设中国特色社会主义法治体系,首先是( )。\", \"trueAnswer\": \"C\", \"options\": {\"A\":\"加强法治专门队伍和法律服务队伍建设\",\"B\":\"要建立严密的法治监督体系\",\"C\":\"要完善以宪法为核心的中国特色社会主义法律体系\",\"D\":\"要加强党内法规制度建设\"}}', '2022-11-20');
INSERT INTO `pratice_test_sheet` VALUES ('4', '“学习《中国共产党章程》”系列专项答题', '专项答题', '{\"id\": \"1\", \"checked\": false, \"question\": \"由旧民主主义革命向新民主主义革命转变的标志是( )。\", \"trueAnswer\": \"C\", \"options\": {\"A\":\"新文化运动\",\"B\":\"辛亥革命\",\"C\":\"“五四”运动\",\"D\":\"中国共产党的成立\"}};\r\n{\"id\": \"2\", \"checked\": false, \"question\": \"党的领导是人民当家作主和依法治国的( )。\", \"trueAnswer\": \"B\", \"options\": {\"A\":\"基本保证\",\"B\":\"根本保证\",\"C\":\"基本特征\",\"D\":\"根本特征\"}};\r\n{\"id\": \"3\", \"checked\": false, \"question\": \"中国特色社会主义进人了新时代,我国经济发展也进入了新时代,基本特征就是经济已由( )。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"高速增长阶段转向高质量发展阶段\",\"B\":\"中国制造向中国创造转变\",\"C\":\"中国速度向中国质量转变\",\"D\":\"制造大国向制造强国转变\"}};\r\n{\"id\": \"4\", \"checked\": false, \"question\": \"“五位一体”将勾勒出一幅( )的社会主义现代化强国壮美景象。\", \"trueAnswer\": \"D\", \"options\": {\"A\":\"富强文明自由和谐美丽\",\"B\":\"富裕民主文明和谐美丽\",\"C\":\"富强民主文明和谐生态\",\"D\":\"富强民主文明和谐美丽\"}};\r\n{\"id\": \"5\", \"checked\": false, \"question\": \"建设中国特色社会主义法治体系,首先是( )。\", \"trueAnswer\": \"C\", \"options\": {\"A\":\"加强法治专门队伍和法律服务队伍建设\",\"B\":\"要建立严密的法治监督体系\",\"C\":\"要完善以宪法为核心的中国特色社会主义法律体系\",\"D\":\"要加强党内法规制度建设\"}}', '2022-11-20');
INSERT INTO `pratice_test_sheet` VALUES ('5', '2022年11月第一周答题', '每周答题', '{\"id\": \"1\", \"checked\": false, \"question\": \"我们党站在时代前列,保持先进性的根本体现和根本要求是( )。\", \"trueAnswer\": \"C\", \"options\": {\"A\":\"始终代表中国先进生产力的发展要求,大力促进先进生产力的发展\",\"B\":\"始终代表中国先进文化的前进方向\",\"C\":\"始终代表中国最广大人民的根本利益\",\"D\":\"始终代表全世界最广大人民的根本利益\"}};\r\n{\"id\": \"2\", \"checked\": false, \"question\": \"科学发展观的核心立场是( )。\", \"trueAnswer\": \"B\", \"options\": {\"A\":\"为人民服务\",\"B\":\"以人为本\",\"C\":\"科学发展\",\"D\":\"以人民为中心\"}};\r\n{\"id\": \"3\", \"checked\": false, \"question\": \"在当代中国,坚持发展是硬道理的本质要求,就是坚持( )。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"科学发展\",\"B\":\"协调发展\",\"C\":\"可持续发展\",\"D\":\"全面发展\"}};\r\n{\"id\": \"4\", \"checked\": false, \"question\": \"科学发展观最鲜明的精神实质是( )。\", \"trueAnswer\": \"C\", \"options\": {\"A\":\"解放思想\",\"B\":\"解放思想、实事求是\",\"C\":\"解放思想、实事求是、与时俱进\",\"D\":\"解放思想、实事求是、与时俱进、求真务实\"}};\r\n{\"id\": \"5\", \"checked\": false, \"question\": \"( )是中国特色社会主义的本质属性。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"经济发达\",\"B\":\"政治清明\",\"C\":\"文化丰富\",\"D\":\"社会和谐\"}}', '2022-11-01');
INSERT INTO `pratice_test_sheet` VALUES ('6', '2022年11月第二周答题', '每周答题', '{\"id\": \"1\", \"checked\": false, \"question\": \"由旧民主主义革命向新民主主义革命转变的标志是( )。\", \"trueAnswer\": \"C\", \"options\": {\"A\":\"新文化运动\",\"B\":\"辛亥革命\",\"C\":\"“五四”运动\",\"D\":\"中国共产党的成立\"}};\r\n{\"id\": \"2\", \"checked\": false, \"question\": \"坚持什么样的改革( ),决定着改革的性质和最终成败。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"方向\",\"B\":\"目标\",\"C\":\"手段\",\"D\":\"措施\"}};\r\n{\"id\": \"3\", \"checked\": false, \"question\": \"习近平法治思想强调,我国社会主义法治之魂是( )。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"党的领导\",\"B\":\"坚持人民主体地位\",\"C\":\"坚持法律面前人人平等\",\"D\":\"坚持从中国实际出发\"}};\r\n{\"id\": \"4\", \"checked\": false, \"question\": \"科学发展观最鲜明的精神实质是( )。\", \"trueAnswer\": \"C\", \"options\": {\"A\":\"解放思想\",\"B\":\"解放思想、实事求是\",\"C\":\"解放思想、实事求是、与时俱进\",\"D\":\"解放思想、实事求是、与时俱进、求真务实\"}};\r\n{\"id\": \"5\", \"checked\": false, \"question\": \"( )是中国特色社会主义的本质属性。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"经济发达\",\"B\":\"政治清明\",\"C\":\"文化丰富\",\"D\":\"社会和谐\"}}', '2022-11-08');
INSERT INTO `pratice_test_sheet` VALUES ('7', '2022年10月第二周答题', '每周答题', '{\"id\": \"1\", \"checked\": false, \"question\": \"( )是社会主义法治最根本的保证。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"党的领导\",\"B\":\"公平正义\",\"C\":\"依法治国\",\"D\":\"社会和谐\"}};\r\n{\"id\": \"2\", \"checked\": false, \"question\": \"新时代党的好干部标准是( )。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"信念坚定、为民服务、勤政务实、敢于担当、清正廉洁\",\"B\":\"有理想、有道德、有文化、有素质\",\"C\":\"年轻化、革命化、专业化\",\"D\":\"政治过硬、经济能干、文化素质高\"}};\r\n{\"id\": \"3\", \"checked\": false, \"question\": \"习近平法治思想强调,我国社会主义法治之魂是( )。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"党的领导\",\"B\":\"坚持人民主体地位\",\"C\":\"坚持法律面前人人平等\",\"D\":\"坚持从中国实际出发\"}};\r\n{\"id\": \"4\", \"checked\": false, \"question\": \"科学发展观最鲜明的精神实质是( )。\", \"trueAnswer\": \"C\", \"options\": {\"A\":\"解放思想\",\"B\":\"解放思想、实事求是\",\"C\":\"解放思想、实事求是、与时俱进\",\"D\":\"解放思想、实事求是、与时俱进、求真务实\"}};\r\n{\"id\": \"5\", \"checked\": false, \"question\": \"( )是中国特色社会主义的本质属性。\", \"trueAnswer\": \"A\", \"options\": {\"A\":\"经济发达\",\"B\":\"政治清明\",\"C\":\"文化丰富\",\"D\":\"社会和谐\"}}', '2022-10-01');
