INSERT INTO `single_option_question` VALUES ('36', 0, '“房子是应该经常打扫的,不打扫就会积满了灰尘,脸是应该经常洗的,不洗也就会灰尘满面。我们同志的思想、我们党的工作,也会沾染灰尘的,也应该打扫和洗涤。”这段话形象地反映了中国共产党在长期革命实践中历形成的( )。', 'D', '{\"A\":\"密切联系群众的优良作风\",\"B\":\"艰苦奋斗的优良作风\",\"C\":\"理论联系实际的优良作风\",\"D\":\"批评与自我批评的优良作风\"}');
INSERT INTO `single_option_question` VALUES ('37', 0, '毛泽东提出“须知政权是由枪杆子中取得的”的著名论断是在( )。', 'A', '{\"A\":\"八七会议\",\"B\":\"古田会议\",\"C\":\"遵义会议\",\"D\":\"瓦窑堡会议\"}');
INSERT INTO `single_option_question` VALUES ('38', 0, '秋收起义后毛泽东创建的第一个农村革命根据地是( )。', 'A', '{\"A\":\"井冈山根据地\",\"B\":\"左右江根据地\",\"C\":\"闽西根据地\",\"D\":\"中央根据地\"}');
INSERT INTO `single_option_question` VALUES ('39', 0, '土地革命战争时期,红色政权能够存在和发展的根本原因是( )。', 'A', '{\"A\":\"中国是一个经济政治发展极端不平衡的半殖民地半封建大国\",\"B\":\"国民革命的政治影响及良好的群众基础\",\"C\":\"全国革命形势的继续发展\",\"D\":\"相当力量的正式红军的存在以及共产党组织的坚强有力领导\"}');
INSERT INTO `single_option_question` VALUES ('4', 0, '中国共产党的历史,是一部不断推进( )的历史,是一部不断推进理论创新、进行理论创造的历史。', 'D', '{\"A\":\"革命\",\"B\":\"建设\",\"C\":\"改革\",\"D\":\"马克思主义中国化\"}');
INSERT INTO `single_option_question` VALUES ('40', 0, '解放战争时期,毛泽东在《在晋绥干部会议上的讲话》中对新民主主义革命的总路线作了完整的概括。这里所说的“完整”是指在革命的对象中增加了( )。', 'D', '{\"A\":\"帝国主义\",\"B\":\"封建主义\",\"C\":\"民族资本主义\",\"D\":\"官僚资本主义\"}');
INSERT INTO `single_option_question` VALUES ('41', 0, '中国共产党要领导革命取得胜利,必须不断加强党的思想建设、组织建设和作风建设。就思想建设而言,中国共产党党内思想上的主要矛盾是( )。', 'A', '{\"A\":\"无产阶级思想和非无产阶级思想之间的矛盾\",\"B\":\"无产阶级思想和资产阶级思想的矛盾\",\"C\":\"无产阶级思想和封建思想的矛盾\",\"D\":\"无产阶级思想和剥削阶级思想的矛盾\"}');
INSERT INTO `single_option_question` VALUES ('42', 0, '中国沦为半殖民地半封建社会的起点的战争是( )。', 'A', '{\"A\":\"鸦片战争\",\"B\":\"第二次鸦片战争\",\"C\":\"甲午中日战争\",\"D\":\"八国联军侵华战争\"}');
INSERT INTO `single_option_question` VALUES ('43', 0, '一九三九年,毛泽东第一次提出“新民主主义革命”的科学概念的著作是( )。', 'A', '{\"A\":\"《中国革命和中国共产党》\",\"B\":\"《在晋绥干部会议上的讲话》\",\"C\":\"《新民主主义论》\",\"D\":\"《论人民民主专政》\"}');
INSERT INTO `single_option_question` VALUES ('44', 0, '中国革命的领导阶级是( )。', 'A', '{\"A\":\"工人阶级\",\"B\":\"农民阶级\",\"C\":\"小资产阶级\",\"D\":\"民族资产阶级\"}');
INSERT INTO `single_option_question` VALUES ('45', 0, '中国革命的中心问题是( )。', 'A', '{\"A\":\"无产阶级的领导权\",\"B\":\"团结革命动力\",\"C\":\"认清革命对象\",\"D\":\"明确革命目标\"}');
INSERT INTO `single_option_question` VALUES ('46', 0, '不属于新民主主义革命时期的统一战线的是( )。', 'D', '{\"A\":\"工农民主统一战线\",\"B\":\"抗日民族统一战线\",\"C\":\"人民民主统一战线\",\"D\":\"新时期爱国统一战线\"}');
INSERT INTO `single_option_question` VALUES ('47', 0, '中国共产党在领导人民革命的过程中,积累了丰富的经验,武装斗争就是战胜敌人的重要法宝之一,其实质是( )。', 'D', '{\"A\":\"无产阶级的反帝国主义战争\",\"B\":\"资产阶级领导的反封建战争\",\"C\":\"工农联合的反军阀战争\",\"D\":\"工人阶级领导的农民战争\"}');
INSERT INTO `single_option_question` VALUES ('48', 0, '无产阶级及其政党在统一战线中必须坚持的原则是( )。', 'D', '{\"A\":\"一切经过统一战线\",\"B\":\"一切服从统一战线\",\"C\":\"坚决斗争\",\"D\":\"独立自主\"}');
INSERT INTO `single_option_question` VALUES ('49', 0, '从鸦片战争到辛亥革命期间,中国人民所进行的反帝反封建的斗争,属于( )。', 'A', '{\"A\":\"旧民主主义革命范畴\",\"B\":\"新民主主义革命范畴\",\"C\":\"过渡时期\",\"D\":\"新民主主义社会\"}');
INSERT INTO `single_option_question` VALUES ('5', 0, '毛泽东晚年的错误是由于违反了他自己主张的( )原则。', 'B', '{\"A\":\"理论联系实际\",\"B\":\"实事求是\",\"C\":\"艰苦奋斗\",\"D\":\"独立自主\"}');
INSERT INTO `single_option_question` VALUES ('50', 0, '1939年7月7日,是卢沟桥事变爆发2周年纪念日,华北联大在延安举行开学典礼,校长成仿吾请毛泽东作报告。毛泽东以他特有的广征博引方式发表讲话,他说:“当年姜子牙下昆仑山,元始天尊赠了他杏黄旗、四不象和打神鞭三样法宝。现在你们出发上前线,我也赠给你们三样法宝。”毛泽东赠给的“三样法宝”是指( )。', 'B', '{\"A\":\"土地革命、武装斗争、农村革命根据地建设\",\"B\":\"统一战线、武装斗争、党的建设\",\"C\":\"实事求是、群众路线、独立自主\",\"D\":\"理论联系实际、密切联系群众、批评与自我批评\"}');
INSERT INTO `single_option_question` VALUES ('51', 0, '中国革命走农村包围城市、武装夺取政权的道路,根本在于处理好( )。', 'A', '{\"A\":\"土地革命、武装斗争、农村革命根据地建设三者之间的关系\",\"B\":\"统一战线、武装斗争、党的建设三者之间的关系\",\"C\":\"工人阶级、农民阶级、民族资产阶级三者之间的关系\",\"D\":\"进步势力、中间势力、顽固势力三者之间的关系\"}');
INSERT INTO `single_option_question` VALUES ('52', 0, '新民主主义革命的前途是( )。', 'B', '{\"A\":\"共产主义\",\"B\":\"社会主义\",\"C\":\"新民主主义\",\"D\":\"资本主义\"}');
INSERT INTO `single_option_question` VALUES ('53', 0, '党的过渡时期总路线的主体是( )。', 'C', '{\"A\":\"三大改造\",\"B\":\"社会主义革命和社会主义建设同时并举\",\"C\":\"社会主义工业化建设\",\"D\":\"对农业的社会主义改造\"}');
INSERT INTO `single_option_question` VALUES ('54', 0, '新民主主义社会在经济上实行国营经济、合作社经济、个体经济、私人资本主义经济和国家资本主义经济五种经济成分并存的经济制度。五种经济成分中,在国民经济中居于领导地位的是( )。', 'A', '{\"A\":\"国营经济\",\"B\":\"个体经济\",\"C\":\"国家资本主义经济\",\"D\":\"私人资本主义经济\"}');
INSERT INTO `single_option_question` VALUES ('55', 0, '一九五三年九月,毛泽东在对民主党派和工商界部分代表讲话时指出:改造资本主义工商业和逐步完成社会主义过渡的必经之路是( )。', 'C', '{\"A\":\"剥夺资本家的财产\",\"B\":\"排挤私营工商业\",\"C\":\"国家资本主义\",\"D\":\"保护民族工商业\"}');
INSERT INTO `single_option_question` VALUES ('56', 0, '我国建国初期属于( )。', 'A', '{\"A\":\"新民主主义社会\",\"B\":\"社会主义社会\",\"C\":\"资本主义社会\",\"D\":\"半殖民地半封建社会\"}');
INSERT INTO `single_option_question` VALUES ('57', 0, '我国在对民族资本主义工商业实行全行业公私合营后采取的赎买政策是( )。', 'B', '{\"A\":\"四马分肥\",\"B\":\"定息\",\"C\":\"加工订货、统购统销\",\"D\":\"国家资本主义\"}');
INSERT INTO `single_option_question` VALUES ('58', 0, '一九五三年提出的过渡时期总路线的过渡时期是指( )。', 'C', '{\"A\":\"从中华人民共和国成立,到社会主义建成\",\"B\":\"从国民经济恢复,到社会主义改造完成\",\"C\":\"从中华人民共和国成立,到社会主义改造基本完成\",\"D\":\"从总路线提出起,到建成社会主义\"}');
INSERT INTO `single_option_question` VALUES ('59', 0, '我国从新民主主义到社会主义的过渡时期,个体经济向社会主义集体经济过渡的形式是( )。', 'C', '{\"A\":\"国营经济\",\"B\":\"私人资本主义经济\",\"C\":\"合作社经济\",\"D\":\"国家资本主义经济\"}');
INSERT INTO `single_option_question` VALUES ('6', 0, '中国共产党推进马克思主义中国化过程中的第一个重大理论成果是( )。', 'B', '{\"A\":\"中国特色社会主义理论体系\",\"B\":\"毛泽东思想\",\"C\":\"邓小平理论\",\"D\":\"新民主主义革命理论\"}');
INSERT INTO `single_option_question` VALUES ('60', 0, '在社会主义改造中,中国共产党对资本主义工商业所采取的政策是( )。', 'D', '{\"A\":\"利用\",\"B\":\"没收\",\"C\":\"限制\",\"D\":\"和平赎买\"}');
INSERT INTO `single_option_question` VALUES ('61', 0, '我国建国初期政权的性质是( )。', 'D', '{\"A\":\"无产阶级专政\",\"B\":\"农民阶级专政\",\"C\":\"资产阶级专政\",\"D\":\"各革命阶级联合专政\"}');
INSERT INTO `single_option_question` VALUES ('62', 0, '党在过渡时期总路线最显著的特点是( )。', 'A', '{\"A\":\"实行社会主义建设和改造同时并举\",\"B\":\"用和平方法改造民族工商业\",\"C\":\"以实现社会主义工业化为主要目标\",\"D\":\"符合当时的基本国情,反映了新民主主义向社会主义转变的历史必然性\"}');
INSERT INTO `single_option_question` VALUES ('63', 0, '在农业合作化进程中,具有半社会主义性质的组织形式是( )。', 'C', '{\"A\":\"临时互助组\",\"B\":\"常年互助组\",\"C\":\"初级合作社\",\"D\":\"高级合作社\"}');
INSERT INTO `single_option_question` VALUES ('64', 0, '我国对个体手工业的社会主义改造,采取了类似改造个体农业的逐步过渡的方法。其中与农业社会主义改造中高级社在性质上相一致的手工业的社会主义改造的形式是( )。', 'C', '{\"A\":\"手工业供销合作社\",\"B\":\"手工业供销小组\",\"C\":\"手工业生产合作社\",\"D\":\"手工业互助组\"}');
INSERT INTO `single_option_question` VALUES ('65', 0, '党在过渡时期的总路线和总任务是( )。', 'B', '{\"A\":\"无产阶级领导的、人民大众的,反对帝国主义、封建主义、官僚资本主义的革命\",\"B\":\"在一相当长的时期内,逐步实现国家的社会主义工业化,并逐步实现国家对农业、手工业和资本主义工商业的社会主义改造\",\"C\":\"鼓足干劲,力争上游,多快好省地建设社会主义\",\"D\":\"以经济建设为中心,坚持四项基本原则,坚持改革开放,自力更生,艰苦创业,为把中国建设成富强、民主、文明的社会主义现代化国家而奋斗。\"}');
INSERT INTO `single_option_question` VALUES ('66', 0, '对资本主义工商业实行社会主义改造,使资本主义所有制过渡到社会主义公有制具有决定意义的重大步骤是( )。', 'D', '{\"A\":\"委托加工\",\"B\":\"计划订货\",\"C\":\"统购包销\",\"D\":\"全行业公私合营\"}');
INSERT INTO `single_option_question` VALUES ('67', 0, '我国在对资本主义工商业进行社会主义改造的过程中,在实行个别企业的公私合营中,企业利润分配上采取的办法是( )。', 'B', '{\"A\":\"定股定息\",\"B\":\"四马分肥\",\"C\":\"和平赎买\",\"D\":\"公私兼顾\"}');
INSERT INTO `single_option_question` VALUES ('68', 0, '建国初期,我国社会主义国营经济建立的最主要的途径和手段是( )。', 'B', '{\"A\":\"没收帝国主义在华企业\",\"B\":\"没收官僚资本\",\"C\":\"没收民族资本\",\"D\":\"没收地主阶级的土地和财产\"}');
INSERT INTO `single_option_question` VALUES ('69', 0, '在新民主主义社会的民族资产阶级仍然是个具有两面性的阶级。这种两面性是指( )。', 'A', '{\"A\":\"既有剥削工人取得利润的一面,又有拥护宪法、愿意接受社会主义改造的一面\",\"B\":\"既有剥削者与被剥削者的阶级利益相互对立的对抗性的一面,又有相互合作、具有相同利益的非对抗性的一面\",\"C\":\"既有走资本主义道路的一面,又有走社会主义道路的一面\",\"D\":\"作为一个剥削阶级是被消灭的对象,作为可以接受共产党和工人阶级领导的社会力量,又是团结和改造的对象\"}');
INSERT INTO `single_option_question` VALUES ('7', 0, '( )为中国送来了马克思列宁主义,帮助中国的先进分子用无产阶级的世界观作为观察国家命运的工具。', 'B', '{\"A\":\"共产国际\",\"B\":\"俄国十月革命的胜利\",\"C\":\"明治维新\",\"D\":\"欧洲1848年革命\"}');
INSERT INTO `single_option_question` VALUES ('70', 0, '在对资本主义工商业改造中,国家对资方在职人员和资方代理人所采取的原则是指( )。', 'A', '{\"A\":\"量才适用,适当照顾\",\"B\":\"改造\",\"C\":\"包下来\",\"D\":\"利用\"}');
INSERT INTO `single_option_question` VALUES ('71', 0, '根据《关于正确处理人民内部矛盾的问题》的分析,对于共产党和民主党派的人民内部矛盾,解决的具体方针是( )。', 'D', '{\"A\":\"“团结-批评-团结”\",\"B\":\"统筹兼顾、适当安排\",\"C\":\"“百花齐放、百家争鸣”\",\"D\":\"“长期共存、互相监督”\"}');
INSERT INTO `single_option_question` VALUES ('72', 0, '我国社会主义制度建立以后,在全党和全国工作重心( )的时候,面临着一个如何认识和处理社会主义社会矛盾的问题。', 'B', '{\"A\":\"由革命转向改造\",\"B\":\"由革命转向建设\",\"C\":\"由革命转向改革\",\"D\":\"由建设转向改革\"}');
INSERT INTO `single_option_question` VALUES ('73', 0, '毛泽东在1957年2月所作的《关于正确处理人民内部矛盾的问题》的报告中,系统论述了社会主义社会矛盾的理论。毛泽东指出:“在社会主义社会中,基本的矛盾仍然是生产关系和生产力之间的矛盾,上层建筑和经济基础之间的矛盾。”社会主义社会的基本矛盾同以往社会的基本矛盾“具有根本不同的性质和情况”。这里所说的“不同的”“情况”是指( )。', 'B', '{\"A\":\"社会主义社会的矛盾反映在政治上可以划分为敌我矛盾和人民内部矛盾\",\"B\":\"社会主义社会基本矛盾运动是“又相适应又相矛盾”\",\"C\":\"社会主义社会基本矛盾运动相适应的一面是非基本方面\",\"D\":\"社会主义社会基本矛盾运动相矛盾的一面是基本方面\"}');
INSERT INTO `single_option_question` VALUES ('74', 0, '《论十大关系》确定的基本方针是( )。', 'B', '{\"A\":\"以苏为鉴\",\"B\":\"努力把党内党外、国内国外的一切积极的因素,直接的、间接的积极因素全部调动起来\",\"C\":\"以经济建设为中心\",\"D\":\"一个中心,两个基本点\"}');
INSERT INTO `single_option_question` VALUES ('75', 0, '毛泽东指出,解决人民内部矛盾的总方针是( )。', 'A', '{\"A\":\"民主的方法\",\"B\":\"“团结-批评-团结”的方法\",\"C\":\"“百花齐放、百家争鸣”的方法\",\"D\":\"“长期共存、互相监督”的方法\"}');
INSERT INTO `single_option_question` VALUES ('76', 0, '新中国的工业化是在( )的影响下起步的。', 'D', '{\"A\":\"英国\",\"B\":\"美国\",\"C\":\"日本\",\"D\":\"苏联\"}');
INSERT INTO `single_option_question` VALUES ('77', 0, '根据《关于正确处理人民内部矛盾的问题》的分析,对于物质利益、分配方面的人民内部矛盾,解决的具体方针是( )。', 'B', '{\"A\":\"“团结-批评-团结”\",\"B\":\"统筹兼顾、适当安排\",\"C\":\"“百花齐放、百家争鸣”\",\"D\":\"“长期共存、互相监督”\"}');
INSERT INTO `single_option_question` VALUES ('78', 0, '根据《关于正确处理人民内部矛盾的问题》的分析,对于科学文化领域里的人民内部矛盾,解决的具体方针是( )。', 'C', '{\"A\":\"“团结-批评-团结”\",\"B\":\"统筹兼顾、适当安排\",\"C\":\"“百花齐放、百家争鸣”\",\"D\":\"“长期共存、互相监督”\"}');
INSERT INTO `single_option_question` VALUES ('79', 0, '判断人们的言论和行动是非的六条标准中最重要的一条( )是。', 'D', '{\"A\":\"经济发展\",\"B\":\"社会进步\",\"C\":\"人民生活水平的提高\",\"D\":\"有利于巩固共产党的领导,而不是摆脱或者削弱这种领导\"}');
INSERT INTO `single_option_question` VALUES ('8', 0, '中国革命和建设取得成功的根本经验是( )。', 'B', '{\"A\":\"独立自主、自力更生\",\"B\":\"把马克思主义与中国具体实际相结合\",\"C\":\"武装斗争、党的建设、统一战线\",\"D\":\"群众路线\"}');
INSERT INTO `single_option_question` VALUES ('80', 0, '中国共产党的第八次全国代表大会是在( )年召开的。', 'C', '{\"A\":\"一九四五\",\"B\":\"一九四九\",\"C\":\"一九五六\",\"D\":\"一九六六\"}');
INSERT INTO `single_option_question` VALUES ('81', 0, '社会主义建设时期,毛泽东提出了以农业为基础,以工业为主导,以( )为序的发展国民经济的总方针。', 'A', '{\"A\":\"农轻重\",\"B\":\"农重轻\",\"C\":\"重农轻\",\"D\":\"重轻农\"}');
INSERT INTO `single_option_question` VALUES ('82', 0, '中国共产党关于社会主义建设道路的探索是( )开始的。', 'B', '{\"A\":\"从探索了工业化的道路后\",\"B\":\"在对苏联模式弊端已有认识的背景下\",\"C\":\"从对社会主义社会基本矛盾的认识基础上\",\"D\":\"从中国社会主义改造道路的实践中\"}');
INSERT INTO `single_option_question` VALUES ('83', 0, '社会主义建设时期,提出“两参一改三结合”思想的党和国家领导人是( )。', 'A', '{\"A\":\"毛泽东\",\"B\":\"周恩来\",\"C\":\"陈云\",\"D\":\"邓小平\"}');
INSERT INTO `single_option_question` VALUES ('84', 0, '中国特色社会主义建设道路的探索始于( )。', 'A', '{\"A\":\"毛泽东\",\"B\":\"邓小平\",\"C\":\"江泽民\",\"D\":\"胡锦涛\"}');
INSERT INTO `single_option_question` VALUES ('85', 0, '20世纪50年代中期,毛泽东明确提出要走一条有别于苏联的中国工业化道路。走中国工业化道路主要是强调要正确处理( )。', 'D', '{\"A\":\"信息化和工业化的关系\",\"B\":\"工业化和农业现代化的关系\",\"C\":\"农业现代化和城镇化的关系\",\"D\":\"重工业和轻工业、农业的关系\"}');
INSERT INTO `single_option_question` VALUES ('86', 0, '敌我矛盾和人民内部矛盾这两类性质完全不同的矛盾,是社会主义社会的矛盾在( )上的反映。', 'A', '{\"A\":\"政治\",\"B\":\"经济\",\"C\":\"文化\",\"D\":\"生态\"}');
INSERT INTO `single_option_question` VALUES ('87', 0, '体现社会主义建设道路初步探索的理论成果的重要著作是( )。', 'D', '{\"A\":\"《中国社会各阶级的分析》和《湖南农民运动考察报告》\",\"B\":\"《实践论》和《矛盾论》\",\"C\":\"《新民主主义论》和《论人民民主专政》\",\"D\":\"《论十大关系》和《关于正确处理人民内部矛盾的问题》\"}');
INSERT INTO `single_option_question` VALUES ('88', 0, '社会主义改造完成进入全面社会主义建设时期,为逐步建立独立的比较完整的基础工业体系和国防工业体系,我们把( )。作为我国经济建设的重点。', 'A', '{\"A\":\"重工业\",\"B\":\"轻工业\",\"C\":\"农业\",\"D\":\"服务业\"}');
INSERT INTO `single_option_question` VALUES ('89', 0, '1956年召开的党的代表大会是党的( )。', 'B', '{\"A\":\"七大\",\"B\":\"八大\",\"C\":\"九大\",\"D\":\"十大\"}');
INSERT INTO `single_option_question` VALUES ('9', 0, '中国共产党的指导思想是( )。', 'C', '{\"A\":\"马克思主义\",\"B\":\"列宁主义\",\"C\":\"马克思列宁主义\",\"D\":\"毛泽东思想\"}');
INSERT INTO `single_option_question` VALUES ('90', 0, '社会主义建设时期,提出“我们的教育方针,应该使受教育者在德育、智育、体育几方面都得到发展,成为有社会主义觉悟的有文化的劳动者”的教育思想的党和国家领导人是( )。', 'A', '{\"A\":\"毛泽东\",\"B\":\"刘少奇\",\"C\":\"周恩来\",\"D\":\"朱德\"}');
INSERT INTO `single_option_question` VALUES ('91', 0, '毛泽东在1957年2月所作的《关于正确处理人民内部矛盾的间题》的报告中,系统论述了社会主义社会矛盾的理论。毛泽东强调,社会主义国家政治生活的主题是( )。', 'B', '{\"A\":\"正确区分和处理两类不同性质矛盾的问题\",\"B\":\"正确处理人民内部矛盾的问题\",\"C\":\"正确处理敌我矛盾的问题\",\"D\":\"正确认识和处理阶级斗争的问题\"}');
INSERT INTO `single_option_question` VALUES ('92', 0, '毛泽东的( ),初步总结了我国社会主义建设的经验,明确提出了以苏为鉴,独立自主地探索适合中国情况的社会主义建设道路。', 'D', '{\"A\":\"《湖南农民运动考察报告》\",\"B\":\"《矛盾论》\",\"C\":\"《新民主主义论》\",\"D\":\"《论十大关系》\"}');
INSERT INTO `single_option_question` VALUES ('93', 0, '社会主义建设时期,党和毛泽东提出了实现四个现代化的宏伟目标,并强调实现四个现代化关键在于( )。', 'D', '{\"A\":\"农业现代化\",\"B\":\"工业现代化\",\"C\":\"国防现代化\",\"D\":\"科学技术现代化\"}');
INSERT INTO `single_option_question` VALUES ('94', 0, '我国经济体制改革的核心问题是如何正确认识和处理( )的关系。', 'A', '{\"A\":\"计划与市场\",\"B\":\"资本主义和社会主义\",\"C\":\"发展速度和效益\",\"D\":\"稳定与发展\"}');
INSERT INTO `single_option_question` VALUES ('95', 0, '邓小平理论是逐步形成和发展起来的。邓小平理论逐步走向成熟始于( )。', 'D', '{\"A\":\"党的十一届三中全会\",\"B\":\"党的十二大\",\"C\":\"党的十三大\",\"D\":\"1992年的南方谈话\"}');
INSERT INTO `single_option_question` VALUES ('96', 0, '党的十三大报告提出了党在社会主义初级阶段的基本路线:领导和团结全国各族人民,以经济建设为中心,坚持四项基本原则,坚持改革开放,自力更生,艰苦创业,为把我国建设成为富强、民主、文明的社会主义现代化国家而奋斗。其中,“以经济建设为中心,坚持四项基本原则,坚持改革开放”(即“一个中心、两个基本点”)是实现社会主义现代化奋斗目标的( )。', 'A', '{\"A\":\"基本途径\",\"B\":\"根本保证\",\"C\":\"依靠力量\",\"D\":\"基本方针\"}');
INSERT INTO `single_option_question` VALUES ('97', 0, '( )是马列主义的精髓,是毛泽东思想的精髓,也是邓小平理论的精髓。', 'A', '{\"A\":\"实事求是\",\"B\":\"解放思想\",\"C\":\"本本主义\",\"D\":\"阶级斗争\"}');
INSERT INTO `single_option_question` VALUES ('98', 0, '邓小平理论形成的时代背景是( )成为时代主题。', 'C', '{\"A\":\"和平\",\"B\":\"发展\",\"C\":\"和平与发展\",\"D\":\"经济建设\"}');
INSERT INTO `single_option_question` VALUES ('99', 0, '( )是社会发展的最根本的决定性因素。', 'A', '{\"A\":\"生产力\",\"B\":\"生产关系\",\"C\":\"阶级斗争\",\"D\":\"劳动力\"}');
INSERT INTO `student` VALUES ('李琛', '32001007', '320', '计算机二党支部', '19858104514', '预备党员');
INSERT INTO `student` VALUES ('尹世超', '32001097', '255', '计算机一党支部', '13095661685', '入党积极分子');
